import React, { Component } from 'react';
import axios from 'axios';
import {COLUMNS} from './columns';
import './table.css';
import {useTable} from 'react-table';
import Cryptotable from './cryptotable';
//import '../../node_modules/font-awesome/css/font-awesome.min.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSort, faSortDown, faSortUp } from '@fortawesome/free-solid-svg-icons'



const sortTypes = {
  up: {
      icon: faSortUp,
      fn: (a, b) => a.Market_Cap - b.Market_Cap 
  },
  down: {
      icon: faSortDown,
      fn: (a, b) => b.Market_Cap - a.Market_Cap 
  }
  
};


export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [],
      cur:[],
      test:[],
      dat: new Date(),
      high:0,
      low:0,
      vol:'',
      mark:'',
      sortDirection:'asc', 
      selectedHeaderIndex:0,
      currentSort: 'up',
      
      //setCryptos:[],
      //lastFetchDate:new Date(),
      //setLastFetchDate:new Date(),
      //searchTerm:'', setSearchTerm:''
    };
    
    
  }

  componentDidMount(){
    axios.get('http://localhost:5000/crypto/')
    .then(response =>{
      const cryptos = response.data;
        this.setState({ cryptos: cryptos, 
          //headers: cryptos[0]
          //test: cryptos.map(name=>name.currency),
        cur:cryptos.Currency,
        dat: new Date(cryptos.Date),
        high:cryptos.High,
        low:cryptos.Low,
        vol:cryptos.Volumn,
        mark:cryptos.Market_Cap,

      });
        console.log(response.data[0]);
    })
    .catch((error)=>{
        console.log(error);
    })

    
  }
  
  cryptoList(){
    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }
  
  onSortChange = () => {
    const currentSort = this.state.currentSort;
    console.log(this.state.currentSort);
    console.log(currentSort);
    let nextSort;

    if (currentSort === 'down') nextSort = 'up';
    else if (currentSort === 'up') nextSort = 'down';
    console.log(nextSort);
    this.setState({
        currentSort: nextSort
    });
  };

  

  render(){
    console.log(this.state.cryptos);//tezos
    console.log(this.props.cryptos); 
    console.log(this.state.currentSort);
    console.log(sortTypes[(this.state.currentSort)].fn);
    
    const data = this.state.cryptos;
    const currentSort = this.state.currentSort;
    console.log(data);
    console.log(currentSort)

    return(
      
      <div>
          <h2>Crypto Currency Historial</h2>
          <table className="table">
              <thead className="thead-light">
                <tr>
                  
                  <th>Currency</th>
                  <th>High</th>
                  <th>Low</th>
                  <th>Volumn</th>
                  <th>MarketCap
                  <button onClick={this.onSortChange}>
                    <FontAwesomeIcon icon={sortTypes[(currentSort)].icon} />
                  </button>
                  </th>
                </tr>
              </thead>
              <tbody>
              {Integer.parseInt([...data]).sort(sortTypes[(currentSort)].fn).map(p=>(
                  <tr>
                    <td>{p.Currency}</td>
                    <td>{p.High}</td>
                    <td>{p.Low}</td>
                    <td>{p.Volume}</td>
                    <td>{p.Market_Cap}</td>
                  </tr>
                )
                )}         
              </tbody>
          </table>
      </div>
  )
    }
  }
