import React, { useMemo,Component } from 'react';
import axios from 'axios';
import {COLUMNS} from './columns';
import './table.css';
import {useTable} from 'react-table';
import {Cryptotable} from './cryptotable';
export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [],
      setCryptos:[],
      lastFetchDate:new Date(),
      setLastFetchDate:new Date(),
      searchTerm:'', setSearchTerm:''
    };
  }

  componentDidMount(){
    axios.get('http://localhost:5000/crypto/')
    .then(response =>{
        this.setState({ cryptos: response.data})
        console.log(response.data)

    })
    .catch((error)=>{
        console.log(error);
    })
  }
  
  cryptoList(){
    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }
  
  

  

  render(){
    return(
      <div>
          <h2>Crypto Currency Historial</h2>
          <table className="table">
              <thead className="thead-light">
                  <tr>
                      <th>Currency</th>
                      <th>Date</th>
                      <th>Open</th>
                      <th>High</th>
                      <th>Low</th>
                      <th>Volume</th>
                      <th>Market Cap</th>
                  </tr>
              </thead>
              <tbody>
                  {this.cryptoList() }
              </tbody>
          </table>
      </div>
  )
    }
  }
