import React, {Component} from 'react';
import axios from 'axios';
import Cryptotable from './cryptotable';
import { useTable } from 'react-table';
import './table.css'

const useSortableData = (items, config = null) => {
    const [sortConfig, setSortConfig] = React.useState(config);
  
    const sortedItems = React.useMemo(() => {
      let sortableItems = [...items];
      if (sortConfig !== null) {
        sortableItems.sort((a, b) => {
          if (a[sortConfig.key] < b[sortConfig.key]) {
            return sortConfig.direction === 'ascending' ? -1 : 1;
          }
          if (a[sortConfig.key] > b[sortConfig.key]) {
            return sortConfig.direction === 'ascending' ? 1 : -1;
          }
          return 0;
        });
      }
      return sortableItems;
    }, [items, sortConfig]);
  
    const requestSort = (key) => {
      let direction = 'ascending';
      if (
        sortConfig &&
        sortConfig.key === key &&
        sortConfig.direction === 'ascending'
      ) {
        direction = 'descending';
      }
      setSortConfig({ key, direction });
    };
  
    return { items: sortedItems, requestSort, sortConfig };
  };

  const getData = axios.get('http://localhost:5000/crypto/')
  .then(response =>{
      console.log(response.data)
  })
  .catch((error)=>{
      console.log(error);
  });

const ProductTable = ()=>{
    const { items, requestSort, sortConfig } = useSortedData(getData);
    const getClassNamesFor = (name) => {
        if (!sortConfig) {
            return;
        }
        return sortConfig.key === name ? sortConfig.direction : undefined;
    };
    return(
        <div>
            <h2>Crypto Currency Historial</h2>
            <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>
                            <button
                                type="button"
                                onClick={() => requestSort('Currency')}
                                className={getClassNamesFor('Currency')}
                            >Currency</button>
                        </th>
                        <th>
                            <button
                                type="button"
                                onClick={() => requestSort('Date')}
                                className={getClassNamesFor('Date')}
                            >Date</button>
                        </th>
                        <th>
                            <button
                                type="button"
                                onClick={() => requestSort('Open')}
                                className={getClassNamesFor('Open')}
                            >Open</button>
                        </th>
                        <th>
                            <button
                                type="button"
                                onClick={() => requestSort('High')}
                                className={getClassNamesFor('High')}
                            >High</button>
                        </th>
                        <th>
                            <button
                                type="button"
                                onClick={() => requestSort('Low')}
                                className={getClassNamesFor('Low')}
                            >Low</button>
                        </th>
                        <th>
                            <button
                                type="button"
                                onClick={() => requestSort('Volume')}
                                className={getClassNamesFor('Volume')}
                            >Volume</button>
                        </th>
                        <th>
                            <button
                                type="button"
                                onClick={() => requestSort(['Market Cap'])}
                                className={getClassNamesFor(['Market Cap'])}
                            >Market Cap</button>
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {items.map((item) => (
                    <tr key={item._id}>
                    <td>{item.Currency}</td>
                    <td>{item.Date}</td>
                    <td>{item.Open}</td>
                    <td>{item.High}</td>
                    <td>{item.Low}</td>
                    <td>{item.Close}</td>
                    <td>{item.Volume}</td>
                    <td>{item.Market_Cap}</td>
                    </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
