import React, { Component } from 'react';
import axios from 'axios';
import Cryptotable from './cryptotable';


function transformCryptos(cryptos) {
  const totalMarketCap = cryptos.reduce((total, crypto) => total + crypto.market_cap, 0);
  return cryptos.map(crypto => ({
    ath_date: crypto.ath_date,
    current_price: `$${parseFloat((crypto.current_price || 0).toFixed(6))}`,
    days_since_ath: Math.floor(daysBetween(new Date(crypto.ath_date), new Date())),
    high_24h: crypto.high_24h || 0,
    image: crypto.image,
    image_symbol: {
      image: crypto.image,
      symbol: crypto.symbol.toUpperCase(),
    },
    low_24h: crypto.low_24h || 0,
    market_cap: crypto.market_cap,
    market_cap_percentage: `${((crypto.market_cap / totalMarketCap) * 100).toFixed(2)}%`,
    market_cap_rank: crypto.market_cap_rank,
    name: crypto.name,
    price_change_24h: crypto.price_change_24h || 0,
    price_change_percentage_24h: `${(crypto.price_change_percentage_24h || 0).toFixed(2)}%`,
    symbol: crypto.symbol.toUpperCase(),
    total_volume: crypto.total_volume || 0,
  }));
}
export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [], 
      setCryptos:[], 
      lastFetchDate:new Date(), 
      setLastFetchDate:new Date(), 
      searchTerm:'', 
      setSearchTerm:'', 
    };
  }
  
  
  componentDidMount(){
      axios.get('http://localhost:5000/crypto/')
      .then(response =>{
          this.setState({ cryptos: response.data})
          console.log(response.data)
      })
      .catch((error)=>{
          console.log(error);
      })
  }

  cryptoList(){
    console.log(this.state.cryptos);
    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }

  render(){
    

    return(
        <div>
            <h2>Crypto Currency Historial</h2>
            <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Currency</th>
                        <th>Date</th>
                        <th>Open</th>
                        <th>High</th>
                        <th>Low</th>
                        <th>Volume</th>
                        <th>Market Cap</th>
                    </tr>
                </thead>
                <tbody>
                    {this.cryptoList() }
                </tbody>
            </table>
        </div>
  )
}

}
