import React, {Component} from 'react';
import axios from 'axios';
import './table.css'


class CryptoList extends Component{
    constructor(props){
        super(props);
        this.state = {cryptos: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5000/crypto/')
        .then(response =>{
            this.setState({ cryptos: response.data})
            console.log(response.data)

        })
        .catch((error)=>{
            console.log(error);
        })
    }

    
    cryptoList(){
        const sortTypes = {
            up: {
                class: 'sort-up',
                fn: (a, b) => a.net_worth - b.net_worth
            },
            down: {
                class: 'sort-down',
                fn: (a, b) => b.net_worth - a.net_worth
            },
            default: {
                class: 'sort',
                fn: (a, b) => a
            }
        };
        state = {
            currentSort: 'default'
        };
        return this.state.cryptos.map(currentcrypto =>{
            const {Currency, Date, Open, High, Low, Close, Volume } = currentcrypto;
            const marketCap = currentcrypto['Market Cap']
            return(
                <tr key={currentcrypto._id}>
                <td>{Currency}</td>
                <td>{Date}</td>
                <td>{Open}</td>
                <td>{High}</td>
                <td>{Low}</td>
                <td>{Close}</td>
                <td>{Volume}</td>
                <td>{marketCap}</td>

            </tr>
            )
        })
    }
    
    render(){
        
        return(
            <div>
                <h2>Crypto Currency Historial</h2>
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>Currency</th>
                            <th>Date</th>
                            <th>Open</th>
                            <th>High</th>
                            <th>Low</th>
                            <th>Close</th>
                            <th>Volume</th>
                            <th>Market Cap</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.cryptoList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
export default CryptoList;
