import React, {Component} from 'react';
import axios from 'axios';
import Cryptotable from './cryptotable';

function SortTable(props){
    const {coin} = props;
    let sortedCoin =[...coin];
    sortedCoin.sort((a, b)=>{
        if (a.crypto['Market Cap'] < b.crypto['Market Cap']){
            return -1;
        }
        if (a.crypto['Market Cap'] > b.crypto['Market Cap']){
            return 1;
        }
    });
        
}
export default class CryptoList extends Component{
    constructor(props){
        super(props);
        this.state = {cryptos: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5000/crypto/')
        .then(response =>{
            this.setState({ cryptos: response.data})
            console.log(response.data)

        })
        .catch((error)=>{
            console.log(error);
        })
    }

    
    cryptoList(){
        const sortedData = SortTable(this.state.cryptos);

        return sortedData.map(currentcrypto =>{
            return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
        })
    }

    render(){
        return(
            <div>
                <h2>Crypto Currency Historial</h2>
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>Currency</th>
                            <th>Date</th>
                            <th>Open</th>
                            <th>High</th>
                            <th>Low</th>
                            <th>Volume</th>
                            <th>Market Cap</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.cryptoList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
