import React, { Fragment, useEffect, useMemo, useState } from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import CryptoTable1 from './CryptoTable1';
import Header from './Header';
import { green, red } from '@material-ui/core/colors';
function daysBetween(from, to) {
  const timeDifference = to.getTime() - from.getTime();
  return timeDifference / (1000 * 3600 * 24);
}
function transformCryptos(cryptos) {
  //const totalMarketCap = cryptos.reduce((total, crypto) => total + crypto.Market_Cap, 0);
  return cryptos.map(crypto => ({
    high: crypto.High || 0,
    low: crypto.Low || 0,
    market_cap: crypto.Market_Cap,
    currency: crypto.Currency,
    volume: crypto.Volume || 0,

  }));//current_price: `$${parseFloat((crypto.current_price || 0).toFixed(6))}`,
  //    market_cap_percentage: `${((crypto.market_cap / totalMarketCap) * 100).toFixed(2)}%`,
  //price_change_percentage_7d: `${(crypto.price_change_percentage_7d || 0).toFixed(2)}%`,
}

const useStyles = makeStyles(theme => ({
  green: {
    color: green[500],
  },
  red: {
    color: red[500],
  },
}));

const CryptoList =()=>{
  const [cryptos, setCryptos] = useState([]);
  const [lastFetchDate, setLastFetchDate] = useState(new Date());
  const [searchTerm, setSearchTerm] = useState('');
  const classes = useStyles();
  
  const columns = useMemo(
    () => [
      {
        Header:'Currency',
        accessor: 'Currency',
        sortType: (a, b, c) => a.values[c].toLowerCase().localeCompare(b.values[c].toLowerCase()), 
        
    },{
        Header:'High',
        accessor: 'High'

    },{
        Header:'Low',
        accessor: 'Low'

    },{
        Header:'Volume',
        accessor: 'Volume'

    },{
        Header:'Market_Cap',
        accessor: 'Market_Cap',
        
    },
    ],
  );

  
  const fetchCryptos = async () => {
    const response = await axios.get('http://localhost:5000/crypto/')
      .then(response =>{
          console.log(response.data)
      })
      .then(d=>{
        console.log("state", d)
      })
      .catch((error)=>{
          console.log(error);
      });
    const json = await response;
    setCryptos(transformCryptos(json));

  }

  useEffect(() => {
    fetchCryptos();
    setTimeout(() => setLastFetchDate(new Date()), 60E3);
  }, [lastFetchDate])


  return(
    <Fragment>
    <Header text="Crypto Prices Dashboard" setSearchTerm={setSearchTerm} />
    <CryptoTable1 columns={columns} data={
      searchTerm
        ? cryptos.filter(crypto => (
          crypto.currency.toLowerCase().includes(searchTerm.toLowerCase())
            || crypto.symbol.includes(searchTerm.toUpperCase())
        ))
        : cryptos
    } />
  </Fragment>
  )
}


export default CryptoList;
