import React, { Component } from 'react';
import axios from 'axios';
import {COLUMNS} from './columns';
import './table.css';
import {useTable} from 'react-table';
import Cryptotable from './cryptotable';
export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [],
      sortDirection:'asc', 
      selectedHeaderIndex:0
      
      //setCryptos:[],
      //lastFetchDate:new Date(),
      //setLastFetchDate:new Date(),
      //searchTerm:'', setSearchTerm:''
    };
    this.ascComparator = (row1, row2) =>
      row1[this.state.selectedHeaderIndex].localeCompare(
        row2[this.state.selectedHeaderIndex]
      );

    this.descComparator = (row1, row2) =>
      row2[this.state.selectedHeaderIndex].localeCompare(
        row1[this.state.selectedHeaderIndex]
      );

    this.flipSortDirection = () =>
      this.state.sortDirection === "asc" ? "desc" : "asc";
  }

  componentDidMount(){
    axios.get('http://localhost:5000/crypto/')
    .then(response =>{
        this.setState({ cryptos: response.data})
        console.log(response.data)
    })
    .catch((error)=>{
        console.log(error);
    })
  }
  
  cryptoList(){
    console.log(this.state.cryptos);
    console.log(this.props.cryptos);

    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }
  
  
  render(){
    const { headers, rows } = this.props.cryptos;

    const comparator =
      this.state.sortDirection === "asc"
        ? this.ascComparator
        : this.descComparator;
    console.log(comparator)
    // sort the rows based on the selected header
    if (comparator){
      const sortedRows = rows.sort(comparator);
      console.log(sortedRows)
    }
    
    


    return(
      <div>
          <h2>Crypto Currency Historial</h2>
          <table className="table">
              <thead className="thead-light">
              {headers.map((header, i) => (
                <th
                  onClick={() => {
                    this.setState({
                      // if we clicked on the already selected index, we flip the sort direction
                      sortDirection:
                        this.state.selectedHeaderIndex === i
                          ? this.flipSortDirection()
                          : "asc",
                      selectedHeaderIndex: i
                    });
                  }}
                >
                {header}
                </th>
              ))}
              </thead>
              <tbody>
                  {this.cryptoList() }
              </tbody>
          </table>
      </div>
  )
    }
  }
