import React, {Component, useMemo} from 'react';
import {useTable} from 'react-table'
import {COLUMNS} from './columns'
import './table.css'

class Cryptotable extends Component{
    BasicTable =() =>{
        const columns = useMemo(()=> COLUMNS, [])
        const data = useMemo(()=> this.state.cryptos, [])
        const tableInstance = useTable({
            columns, 
            data
        })

        const {
            getTableProps, 
            getTableBodyProps, 
            headerGroups, 
            rows, 
            prepareRow
        } = tableInstance

        return(
            <table {...getTableProps}>
                <thead>
                    {headerGroups.map((headerGroup)=>(
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map((column)=>(
                                <th {...column.getHeaderProps()}>
                                    {column.render('Header')}
                                </th>
                        ))} 
                        </tr>
                    ))}
                    
                </thead>
                <tbody {...getTableBodyProps}>
                    {rows.map((row) =>{
                        prepareRow(row)
                        return(
                            <tr {...row.getRowProps()}>
                                {row.cells.map((cell)=>{
                                    return <td {...cell.getCellProps()}>
                                        {cell.render('Cell')}
                                    </td>
                                })}
                            </tr>

                        )
                    })} 
                </tbody>
            </table>
        )
}
    render(){
        return(
            <tr>
                <td>{this.props.crypto.Currency}</td>
                <td>{this.props.crypto.Date}</td>
                <td>{this.props.crypto.Open}</td>
                <td>{this.props.crypto.High}</td>
                <td>{this.props.crypto.Low}</td>
                <td>{this.props.crypto.Volume}</td>
                <td>{this.props.crypto['Market Cap']}</td>
            </tr>
        );
    }
    
}
export default Cryptotable;
