import React, { Component } from 'react';
import axios from 'axios';
import './table.css';
import Cryptotable from './cryptotable';

const useStyles = makeStyles(theme => ({
  green: {
    color: green[500],
  },
  red: {
    color: red[500],
  },
}));
export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {cryptos: []};
    const [cryptos, setCryptos] = useState([]);
    const [lastFetchDate, setLastFetchDate] = useState(new Date());
    const [searchTerm, setSearchTerm] = useState('');
    const classes = useStyles();

  }
  
  componentDidMount(){
      axios.get('http://localhost:5000/crypto/')
      .then(response =>{
          this.setState({ cryptos: response.data})
          console.log(response.data)
      })
      .catch((error)=>{
          console.log(error);
      })
  }

  cryptoList(){
    console.log(this.state.cryptos);
    console.log(typeof(this.state.cryptos));

    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }

  render(){
    return(
        <div>
            <h2>Crypto Currency Historial</h2>
            <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Currency</th>
                        <th>Date</th>
                        <th>Open</th>
                        <th>High</th>
                        <th>Low</th>
                        <th>Volume</th>
                        <th>Market Cap</th>
                    </tr>
                </thead>
                <tbody>
                    {this.cryptoList() }
                </tbody>
            </table>
        </div>
  )
}

}
