import React, { Component } from 'react';
import axios from 'axios';
import {COLUMNS} from './columns';
import './table.css';
import {useTable} from 'react-table';
import Cryptotable from './cryptotable';
//import '../../node_modules/font-awesome/css/font-awesome.min.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSort, faSortDown, faSortUp } from '@fortawesome/free-solid-svg-icons'



const sortTypes = {
  up: {
      icon: faSortUp,
      fn: (a, b) => a.Market_Cap - b.Market_Cap ?1:-1
  },
  down: {
      icon: faSortDown,
      fn: (a, b) => b.Market_Cap - a.Market_Cap ?-1:1
  },
  default: {
      icon: faSort,
      fn: (a, b) => a
  }
};


export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [],
      cur:[],
      test:[],
      dat: new Date(),
      high:0,
      low:0,
      vol:'',
      mark:'',
      sortDirection:'asc', 
      selectedHeaderIndex:0,
      currentSort: 'default',
      
      //setCryptos:[],
      //lastFetchDate:new Date(),
      //setLastFetchDate:new Date(),
      //searchTerm:'', setSearchTerm:''
    };
    
    /*
    this.ascComparator = (row1, row2) =>
      row1[this.state.selectedHeaderIndex].localeCompare(
        row2[this.state.selectedHeaderIndex]
      );

    this.descComparator = (row1, row2) =>
      row2[this.state.selectedHeaderIndex].localeCompare(
        row1[this.state.selectedHeaderIndex]
      );

    this.flipSortDirection = () =>
      this.state.sortDirection === "asc" ? "desc" : "asc";
      */
  }

  componentDidMount(){
    axios.get('http://localhost:5000/crypto/')
    .then(response =>{
      const cryptos = response.data;
        this.setState({ cryptos: cryptos, 
          //headers: cryptos[0]
          //test: cryptos.map(name=>name.currency),
        cur:cryptos.Currency,
        dat: new Date(cryptos.Date),
        high:cryptos.High,
        low:cryptos.Low,
        vol:cryptos.Volumn,
        mark:cryptos.Market_Cap,

      });
        console.log(response.data[0]);
        //return response.data
    })
    .catch((error)=>{
        console.log(error);
    })

    
  }
  
  cryptoList(){
    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }
  
  onSortChange = () => {
    const currentSort = this.state.currentSort;
    console.log(this.state.currentSort);
    console.log(currentSort);
    let nextSort;

    if (currentSort === 'down') nextSort = 'up';
    else if (currentSort === 'up') nextSort = 'default';
    else if (currentSort === 'default') nextSort = 'down';
    console.log(nextSort);
    this.setState({
        currentSort: nextSort
    });
  };

  

  render(){
    console.log(this.state.cryptos);//tezos
    console.log(this.props.cryptos); 
    console.log(this.state.currentSort);
    console.log(sortTypes[(this.state.currentSort)].fn);
    /*const table = {
      headers:["Currency", "High", "Low", "Volumn", "Market_Cap"],
      rows:[[this.state.cryptos.cur], 
            [this.state.cryptos.high],
            [this.state.cryptos.low],
            [this.state.cryptos.vol],
            [this.state.cryptos.mark],
            ]
    };
    
    const {headers, rows } = table;
    
    const comparator =
      this.state.sortDirection === "asc"
        ? this.ascComparator
        : this.descComparator;
    const sortedRows = rows.sort(comparator);
*/
    //<FontAwesomeIcon icon={`fas fa-${sortTypes[(this.state.currentSort)].icon}`} />
    const data = this.state.cryptos;
    const currentSort = this.state.currentSort;
    console.log(data);
    console.log(currentSort)

    return(
      
      <div>
          <h2>Crypto Currency Historial</h2>
          <table className="table">
              <thead className="thead-light">
                <tr>
                  
                  <th>Currency</th>
                  <th>High</th>
                  <th>Low</th>
                  <th>Volumn</th>
                  <th>MarketCap
                  <button onClick={this.onSortChange}>
                    <FontAwesomeIcon icon={sortTypes[(currentSort)].icon} />
                  </button>
                  </th>
                </tr>
              </thead>
              <tbody>
              {[...data].sort(sortTypes[(this.state.currentSort)].fn).map(p=>(
                  <tr>
                    <td>{p.Currency}</td>
                    <td>{p.High}</td>
                    <td>{p.Low}</td>
                    <td>{p.Volume}</td>
                    <td>{p.Market_Cap}</td>
                  </tr>
                )
                )}         
              </tbody>
          </table>
      </div>
  )
    }
  }
  
/*{headers.map((header, i) => (
                <th
                  onClick={() => {
                    this.setState({
                      // if we clicked on the already selected index, we flip the sort direction
                      sortDirection:
                        this.state.selectedHeaderIndex === i
                          ? this.flipSortDirection()
                          : "asc",
                      selectedHeaderIndex: i
                    });
                  }}
                >
                  {header}
                </th>
              ))}*/
  /*{sortedRows.map(row => (
                  <tr>
                    {row.map(cell => (
                      <td>{cell}</td>
                    ))}
                  </tr>
                ))}*/
