import React, { Component } from 'react';
import axios from 'axios';
import {COLUMNS} from './columns';
import './table.css';
import {useTable} from 'react-table';
import Cryptotable from './cryptotable';
//import '../../node_modules/font-awesome/css/font-awesome.min.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSort, faSortDown, faSortUp } from '@fortawesome/free-solid-svg-icons'



const sortTypes = {
  up: {
      icon: faSortUp,
      fn: (a, b) => a.Date - b.Date 
  },
  down: {
      icon: faSortDown,
      fn: (a, b) => b.Date - a.Date 
  },
  default:{
      icon: faSort,
      fn: (a, b) => a 
  }
  
};


export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [],
      sortNameCryptos:[],
      cur:[],
      test:[],
      dat: new Date(),
      high:0,
      low:0,
      vol:'',
      mark:'',
      sortDirection:'asc', 
      selectedHeaderIndex:0,
      currentSort: 'default',
      
      //setCryptos:[],
      //lastFetchDate:new Date(),
      //setLastFetchDate:new Date(),
      //searchTerm:'', setSearchTerm:''
    };
    
    
  }
  removeDuplicates(arr) {
    const map = new Map();
    arr.forEach(v => map.set(v.Currency, v)) 
    return [...map.values()];
  }
  
  componentDidMount(){
    axios.get('http://localhost:5000/crypto/')
    .then(response =>{
      const cryptos = response.data;
        this.setState({ 
          cryptos: cryptos, 
          //headers: cryptos[0]
          //test: cryptos.map(name=>name.currency),
        cur:cryptos.Currency,
        dat: new Date(cryptos.Date),
        high:cryptos.High,
        low:cryptos.Low,
        vol:cryptos.Volumn,
        mark:cryptos.Market_Cap,

      });
        console.log(response.data[0]);
    })
    .catch((error)=>{
        console.log(error);
    })

    
  }
  
  cryptoList(){
    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }
  
  onSortChange = () => {
    const currentSort = this.state.currentSort;
    console.log(this.state.currentSort);
    console.log(currentSort);
    let nextSort;

    if (currentSort === 'down') nextSort = 'up';
    else if (currentSort === 'up') nextSort = 'default';
    else if (currentSort === 'default') nextSort = 'down';
    console.log(nextSort);
    this.setState({
        currentSort: nextSort
    });
  };

  compareBy(key) {
    return function (a, b) {
      if (a[key] < b[key]) return -1;
      if (a[key] > b[key]) return 1;
      return 0;
    };
  }

  sortBy(key) {
    let arrayCopy = [...this.state.cryptos ];
    arrayCopy.sort(this.compareBy(key));
    this.setState({cryptos: arrayCopy});
  }
  
  filterDate=()=>{
    const data = this.state.cryptos;
    data.sort(this.compareBy(data.Date));
    
  };
  render(){
    console.log(this.state.cryptos);//tezos
    console.log(this.props.cryptos); 
    console.log(this.state.currentSort);
    console.log(sortTypes[(this.state.currentSort)].fn);
    
    const data = this.state.cryptos;
    const currentSort = this.state.currentSort;
    console.log(data);
    console.log(currentSort)
    /*const myData = [].concat(this.state.cryptos)
    .sort((a, b) => a.Market_Cap > b.Market_Cap ? 1 : -1)
    .map((item, i) => 
      <tr key={item._id}>
      <td>{item.Currency}</td>
      <td>{item.High}</td>
      <td>{item.Low}</td>
      <td>{item.Volume}</td>
      <td>{item.Market_Cap}</td>
      </tr>
    );*/
    const driver =this.state.cryptos;
    return(
      
      <div>
          <h2>Crypto Currency Historial</h2>
          <table className="table">
              <thead className="thead-light">
                <tr>
                  <th>Number</th>
                  <th>Currency</th>
                  <th onClick={() => this.sortBy('Date')}>Date</th>
                  <th onClick={() => this.sortBy('Market_Cap')}>Market Cap</th>
                  
                </tr>
              </thead>
              <tbody>
              {driver.map((c, i) => (
              <tr key={c._id}>
                <td>{i + 1}</td>
                <td style={{ color: "#b71c1c" }} className="font-weight-bolder">
                  {c.Currency ? `${c.Currency}` : " ? "}
                </td>
                <td>{c.Date ? `${c.Date}` : "-"} </td>
                <td>{c.Market_Cap ? `${c.Market_Cap} ` : "-"} </td>
                </tr>
              ))}
              </tbody>
          </table>
      </div>
  )
    }
  }
  /*<th>MarketCap
                  <button onClick={this.onSortChange}>
                    <FontAwesomeIcon icon={sortTypes[(currentSort)].icon} />
                  </button>*/
/*{[...data].sort(sortTypes[(currentSort)].fn).map(p=>(
                  <tr>
                    <td>{p.Currency}</td>
                    <td>{p.High}</td>
                    <td>{p.Low}</td>
                    <td>{p.Volume}</td>
                    <td>{p.Market_Cap}</td>
                  </tr>
                )
                )}*/
