import React, {Component} from 'react';
import axios from 'axios';

const Crypto = props =>(
    <tr>
        <td>{props.crypto.currency}</td>
        
    </tr>
)
export default class CryptoList extends Component{
    constructor(props){
        super(props);
        this.state = {cryptos: []};

    }

    componentDidMount(){
        axios.get('http://localhost:5000/crypto/')
        .then(response =>{
            this.setState({ cryptos: response.data})
            console.log(response.data)
        })
        .catch((error)=>{
            console.log(error);
        })
    }

    cryptoList(){
        return this.state.cryptos.map(currentcrypto=>{
            return <Crypto crypto={currentcrypto} key={currentcrypto._id}/>;
        })
    }

    render(){
        return(
            <div>
                <h2>Crypto Currency Historial</h2>
                <table className="table">
                    <thead className="thread-light">
                        <tr>
                            <th>Currency</th>
                            <th>Date</th>
                            <th>Open</th>
                            <th>High</th>
                            <th>Low</th>
                            <th>Volume</th>
                            <th>Market Cap</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.cryptoList()
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}
