import React, {Component} from 'react';
import axios from 'axios';
import './table.css'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


const sortTypes = {
    up: {
        class: 'sort-up',
        fn: (a, b) => a['Market Cap'] - b['Market Cap']
    },
    down: {
        class: 'sort-down',
        fn: (a, b) => b['Market Cap'] - a['Market Cap']
    },
    default: {
        class: 'sort',
        fn: (a, b) => a
    }
};
class CryptoList extends Component{

    
    state = {
        currentSort: 'default'
    };
    onSortChange = () => {
        const { currentSort } = this.state;
        let nextSort;

        if (currentSort === 'down') nextSort = 'up';
        else if (currentSort === 'up') nextSort = 'default';
        else if (currentSort === 'default') nextSort = 'down';

        this.setState({
            currentSort: nextSort
        });
    };
    constructor(props){
        super(props);
        this.state = {cryptos: []};
    }

    componentDidMount(){
        axios.get('http://localhost:5000/crypto/')
        .then(response =>{
            this.setState({ cryptos: response.data})
            console.log(response.data)

        })
        .catch((error)=>{
            console.log(error);
        })
    }

    
    /*cryptoList(){
        return this.state.cryptos.sort(currentSort.fn).map(currentcrypto =>{
            const {Currency, Date, Open, High, Low, Close, Volume } = currentcrypto;
            const marketCap = currentcrypto['Market Cap']
            return(
                <tr key={currentcrypto._id}>
                <td>{Currency}</td>
                <td>{Date}</td>
                <td>{Open}</td>
                <td>{High}</td>
                <td>{Low}</td>
                <td>{Close}</td>
                <td>{Volume}</td>
                <td>{marketCap}</td>

            </tr>
            )
        })
    }*/
    
    render(){
        
        
        const {data} =this.state.cryptos;
        return(
            <div>
                <h2>Crypto Currency Historial</h2>
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>Currency</th>
                            <th>Date</th>
                            <th>Open</th>
                            <th>High</th>
                            <th>Low</th>
                            <th>Close</th>
                            <th>Volume</th>
                            <th>
                                Market Cap
                                <button onClick={this.onSortChange}>
                                    <FontAwesomeIcon icon={`fas fa-${sortTypes[currentSort].class}`}/>
                                </button>
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {[...data].sort(sortTypes[currentSort].fn).map(currentcrypto =>{
                            <tr key={currentcrypto._id}>
                                <td>{currentcrypto.Currency}</td>
                                <td>{currentcrypto.Date}</td>
                                <td>{currentcrypto.Open}</td>
                                <td>{currentcrypto.High}</td>
                                <td>{currentcrypto.Low}</td>
                                <td>{currentcrypto.Close}</td>
                                <td>{currentcrypto.Volume}</td>
                                <td>{currentcrypto['Market Cap']}</td>
                            </tr>
                        })
                        }
                        //{this.cryptoList() }
                    </tbody>
                </table>
            </div>
        )
    }
}
export default CryptoList;
