import React, { Component } from 'react';
import axios from 'axios';
import Cryptotable from './cryptotable';


export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [], 
      setCryptos:[], 
      lastFetchDate:new Date(), 
      setLastFetchDate:new Date(), 
      searchTerm:'', 
      setSearchTerm:'', 
    };
  }
  
  componentDidMount(){
      axios.get('http://localhost:5000/crypto/')
      .then(response =>{
          this.setState({ cryptos: response.data})
          console.log(response.data)
      })
      .catch((error)=>{
          console.log(error);
      })
  }

  cryptoList(){
    console.log(this.state.cryptos);
    console.log(typeof(this.state.cryptos));

    return this.state.cryptos.map(currentcrypto =>{
        return <Cryptotable crypto={currentcrypto} key={currentcrypto._id}/>;
    })
  }

  render(){
    const columns = useMemo(
      () => [
        {
          Header: 'Rank',
          accessor: 'market_cap_rank',
        },
        {
          Header: 'Symbol',
          accessor: 'image_symbol',
          Cell: ({ cell: { value } }) => (
            <span>
              <img alt="" src={value.image} width={13} />
              <span style={{ marginLeft: 10 }}><b>{value.symbol}</b></span>
            </span>
          ),
          sortType: (a, b, c) => a.values[c].symbol.localeCompare(b.values[c].symbol),
        },
        {
          Header: 'Name',
          accessor: 'name',
          sortType: (a, b, c) => a.values[c].toLowerCase().localeCompare(b.values[c].toLowerCase()),
        },
        {
          Header: 'Price',
          accessor: 'current_price',
        },
        {
          Header: '24h',
          accessor: 'price_change_percentage_24h',
          Cell: ({ cell: { value } }) => (
            <span className={value[0] === '-' ? classes.red : classes.green}>
              {value[0] === '-' ? value.slice(1) : value}
            </span>
          ),
          sortType: (a, b, c) => parseFloat(a.values[c]) - parseFloat(b.values[c]),
        },
        {
          Header: 'Market Share',
          accessor: 'market_cap_percentage',
        },
        {
          Header: 'Days Since ATH',
          accessor: 'days_since_ath',
        },
      ],
      [classes.green, classes.red]
    );
    return(
        <div>
            <h2>Crypto Currency Historial</h2>
            <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Currency</th>
                        <th>Date</th>
                        <th>Open</th>
                        <th>High</th>
                        <th>Low</th>
                        <th>Volume</th>
                        <th>Market Cap</th>
                    </tr>
                </thead>
                <tbody>
                    {this.cryptoList() }
                </tbody>
            </table>
        </div>
  )
}

}
