import React, { Component } from 'react';
import axios from 'axios';
import {COLUMNS} from './columns';
import './table.css';
import {useTable} from 'react-table';
import Cryptotable from './cryptotable';
//import '../../node_modules/font-awesome/css/font-awesome.min.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSort, faSortDown, faSortUp } from '@fortawesome/free-solid-svg-icons'
import Moment from 'moment';


const sortTypes = {
  up: {
      icon: faSortUp,
  },
  down: {
      icon: faSortDown,
  },
  default:{
      icon: faSort,
  }
  
};


export default class CryptoList extends Component{
  constructor(props){
    super(props);
    this.state = {
      cryptos: [],
      latestDatecryptos:[],
      sortNameCryptos:[],
      sevenday:[],
      test:[],
      sortDirection:'asc', 
      selectedHeaderIndex:0,
      currentSort: 'default',
      
    };
    
    
  }
 
  
  componentDidMount(){
    axios.get('http://localhost:5000/crypto/')
    .then(response =>{
      const cryptos = response.data;
        this.setState({ 
          cryptos: cryptos, 
          latestDatecryptos:cryptos.filter(data=>data.Date==cryptos[0].Date),

        });
        console.log(response.data[0]);
    })
    .catch((error)=>{
        console.log(error);
    })

    
  }
  
  compareBy(key) {
    return function (a, b) {
      if (parseInt(a[key].replace(/,/g,'')) < parseInt(b[key].replace(/,/g,''))) return -1;
      if (parseInt(a[key].replace(/,/g,'')) > parseInt(b[key].replace(/,/g,''))) return 1;
      return 0;
    };
  }

  sortBy(key) {
    let arrayCopy = [...this.state.latestDatecryptos ];  
    arrayCopy.sort(this.compareBy(key));
    this.setState({latestDatecryptos: arrayCopy});
    console.log(arrayCopy)

    const { currentSort } = this.state.currentSort;
		let nextSort;
		if (currentSort === 'down') nextSort = 'up';
		else if (currentSort === 'up') nextSort = 'default';
		else if (currentSort === 'default') nextSort = 'down';
		this.setState({
			currentSort: nextSort
		});
  }

 
  
  checkOldDate(oldDate, close, coin){
    let arrayOldDate = [...this.state.cryptos.filter(data=>{ 
      return data.Date===oldDate && data.Currency===coin
    }) ];
    console.log(arrayOldDate);

    const open7=arrayOldDate[0].Open;
    console.log(open7);
    const res = (close - open7)/open7 *100; 
    console.log(res.toFixed(2));
    return res.toFixed(2);
  }

  sevenDay(close, coin){
    let arraynow = [...this.state.latestDatecryptos ];
    console.log(arraynow);
    const a = Moment(arraynow[0].Date).format('MMM DD, YYYY');
    console.log(a);
    const b = Moment(arraynow[0].Date).subtract(1, 'weeks').format('MMM DD, YYYY');
    console.log(b);
    return this.checkOldDate(b, close, coin);
  }

  oneDay(close, coin){
    let arraynow = [...this.state.latestDatecryptos ];
    console.log(arraynow);
    const a = Moment(arraynow[0].Date).format('MMM DD, YYYY');
    console.log(a);
    const b = Moment(arraynow[0].Date).subtract(1, 'days').format('MMM DD, YYYY');
    console.log(b);
    return this.checkOldDate(b, close, coin);

  }
  
  render(){
    console.log(this.state.cryptos);//tezos
    console.log(this.props.cryptos); 
    console.log(this.state.dat); 
    console.log(this.state.test);
    console.log(this.state.currentSort);
    if (this.state.cryptos.length > 0) {
      
      const latestDate = this.state.cryptos[0].Date;
      const newData = this.state.cryptos.filter(data=>data.Date===latestDate);
      console.log(latestDate)
      console.log(newData)

    }
    console.log(this.state.latestDatecryptos);
    
    

    const currentSort = this.state.currentSort;
    console.log(currentSort)
    
    const driver =this.state.latestDatecryptos;

    return(
      
      <div>
          <h2>Crypto Currency Historial</h2>
          <table className="table">
              <thead className="thead-light">
                <tr>
                  <th>Number</th>
                  <th>Currency</th>
                  <th>1d</th>
                  <th>7d</th>
                  <th onClick={() => this.sortBy('Volume')}>Volume</th>
                  <th >Market Cap
                    <button onClick={this.sortBy('Market_Cap')}>
                      <FontAwesomeIcon icon={sortTypes[(currentSort)].icon} />
                    </button>
                  </th>
                  
                </tr>
              </thead>
              <tbody>
              {driver.map((c, i) => (
              <tr key={c._id}>
                <td>{i + 1}</td>
                <td style={{ color: "#b71c1c" }} className="font-weight-bolder">
                  {c.Currency ? `${c.Currency}` : " ? "}
                </td>
                <td>{this.oneDay(c.Close, c.Currency) ? this.oneDay(c.Close, c.Currency) : "-"} %</td>
                <td>{this.sevenDay(c.Close, c.Currency) ? this.sevenDay(c.Close, c.Currency) : "-"} %</td>
                <td>{c.Volume ? `${c.Volume} ` : "-"} </td>
                <td>{c.Market_Cap ? `${c.Market_Cap} ` : "-"} </td>
                </tr>
              ))}
              
              </tbody>
          </table>
      </div>
  )
    }
  }
